<?php
define('PRECISION', 3);

function convert($source)
{
    $data = array();
    foreach (explode(', ', $source) as $pair) {
        $values = explode(' ', $pair);

        $ax = 0.01653;
        $ay = 0.01295;

        $dx = 52.413;
        $dy = 42.9;

        $lat = $dy - $ay * $values[1];
        $lng = $dx + $ax * $values[0];

        $lat = number_format(round($lat, PRECISION), PRECISION, '.', '');
        $lng = number_format(round($lng, PRECISION), PRECISION, '.', '');

        $data[] = $lat . ', ' . $lng;
    }

    return '[' . implode('], [', $data) . ']';
}

